const API_BASE_URL = process.env.VUE_APP_ADMIN_API_URL || 'http://20.121.46.47:7777/api';

async function login(username, password) {
  const response = await fetch(`${API_BASE_URL}/auth/login`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ username, password }),
  });

  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || `HTTP error! status: ${response.status}`);
  }

  if (!data.success) {
    throw new Error(data.message || 'Login failed. Please check your credentials.');
  }
  return data; // Expects { success: true, user: { username: '...' } }
}

// Placeholder for other API functions
// async function getMessages(token, filters = {}, page = 1, limit = 10) { ... }
// async function deleteMessage(token, messageId) { ... }
// async function getRooms(token, page = 1, limit = 10) { ... }
// async function deleteRoom(token, roomId) { ... }

export default {
  login,
  // getMessages,
  // deleteMessage,
  // getRooms,
  // deleteRoom,
};
